hashmap = dict()


def sphere_func(x, y, z):
    return 1 * math.sqrt((x - 0.5)**2 + (y - 0.5)**2 + (z - 0.5)**2)

def hash(arr):
    return (arr[0] * res * res) + (arr[1] * res) + arr[2]

def evaluate(f, arr):
    h = hash(arr)
    if h in hashmap.keys():
        return hashmap[h]
    x = arr[0]
    y = arr[1]
    z = arr[2]
    val = f(x, y, z)
    hashmap[h] = val
    return val

def mean(v1, v2, interpolate = False, f = None):
    weight1 = 1
    weight2 = 1
    if interpolate:
        if f is None:
            raise Exception("no function given")
        weight2 = (surfaceLevel - evaluate(f, v1)) / (evaluate(f, v2) - evaluate(f, v1))
        weight1 = 1 - weight2
    result = [0, 0, 0]
    for i in range(3):
        result[i] = ((weight1 * v1[i]) + (weight2 * v2[i])) / (weight1 + weight2)
    return result

def helper(f, x, y, dx, dy, dz, z_min, verticesX, verticesY, verticesZ, facesA, facesB, facesC):
    for k in range(res):
        z = z_min + (k * dz)
        #corners = [[x, y, z], [x+dx, y, z], [x, y+dy, z], [x+dx, y+dy, z], [x, y, z+dz], [x+dx, y, z+dz], [x, y+dy, z+dz], [x+dz, y+dy, z+dz]]
        corners = [[x, y, z], [x+dx, y, z], [x+dx, y, z+dz], [x, y, z+dz], [x, y+dy, z], [x+dx, y+dy, z], [x+dx, y+dy, z+dz], [x, y+dy, z+dz]]
        cubeIndex = 0
        for cornerNum in range(len(corners)):
            if evaluate(f, corners[cornerNum]) < surfaceLevel:
                cubeIndex |= 1 << cornerNum
        trianglulation = triTable[cubeIndex]
        if len(trianglulation) % 3 != 0:
            raise Exception("error in triangulation table")
        for faceNum in range(len(trianglulation) // 3):
            #prevNumVertices = len(vertices)
            #buf = []
            for vertexNum in range(3):
                edgeIndex = trianglulation[(faceNum * 3) + vertexNum]
                indexA = cornersFromEdge[0][edgeIndex]
                indexB = cornersFromEdge[1][edgeIndex]
                vertex = mean(corners[indexA], corners[indexB], True, f)
                verticesX[(k * 15) + (faceNum * 3) + vertexNum] = vertex[0]
                verticesY[(k * 15) + (faceNum * 3) + vertexNum] = vertex[1]
                verticesZ[(k * 15) + (faceNum * 3) + vertexNum] = vertex[2]
                #buf.append(vertex)
            #lock.acquire()
            #vertices.extend(buf)
            #faces.append([prevNumVertices, prevNumVertices+1, prevNumVertices+2])
            facesA[(k * 5) + faceNum] = (faceNum * 3) + 1
            facesB[(k * 5) + faceNum] = (faceNum * 3) + 2
            facesC[(k * 5) + faceNum] = (faceNum * 3) + 3
            #lock.release()

def march(f, x_min=0, x_max = 1, y_min=0, y_max=1, z_min=0, z_max=1):
    #lock = Lock()

    #print(indicator_function(1, 0.5, 0.5))
    dx = (x_max - x_min) / res
    dy = (y_max - y_min) / res
    dz = (z_max - z_min) / res

    

    threads = []

    for i in range(res):
        x = x_min + (i * dx)
        for j in range(res):
            y = y_min + (j * dy)
            #helper(f, x, y, dx, dy, dz, z_min, vertices, faces, lock)

            verticesX = Array('d', [x_min - 1.0] * 15 * res)
            verticesY = Array('d', [y_min - 1.0] * 15 * res)
            verticesZ = Array('d', [z_min - 1.0] * 15 * res)
            facesA = Array('i', [-1] * 5 * res)
            facesB = Array('i', [-1] * 5 * res)
            facesC = Array('i', [-1] * 5 * res)

            threads.append([Process(target=helper, args=(f, x, y, dx, dy, dz, z_min, verticesX, verticesY, verticesZ, facesA, facesB, facesC)), verticesX, verticesY, verticesZ, facesA, facesB, facesC])
            threads[-1][0].start()

    toc = time.time()
    print(toc - tic)
    
    vertices = []
    faces = []

    for thread in threads:
        thread[0].join()
        verticesX = thread[1]
        verticesY = thread[2]
        verticesZ = thread[3]
        facesA = thread[4]
        facesB = thread[5]
        facesC = thread[6]
        for faceNum in range(len(facesA)):
            if facesA[faceNum] != -1:
                prevNumVertices = len(vertices)
                for i in range(3):
                    index = (faceNum * 3) + i
                    vertex = [verticesX[index], verticesY[index], verticesZ[index]]
                    vertices.append(vertex)
                faces.append([prevNumVertices, prevNumVertices+1, prevNumVertices+2])


    #print(vertices)
    #print(faces)
    mesh = trimesh.Trimesh(vertices=vertices, faces=faces)
    #trimesh.repair.fix_winding(mesh)
    tac = time.time()
    print(tac - toc)
    mesh.show()






